(window.webpackJsonp=window.webpackJsonp||[]).push([[138],{518:function(t,n,r){"use strict";r.r(n);var s=r(42),o=Object(s.a)({},(function(){var t=this,n=t.$createElement,r=t._self._c||n;return r("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[r("p",[t._v("算法工程师是否应该持续读论文")]),t._v(" "),r("p",[r("strong",[t._v("个人建议，无论是科研型的还是业务型的，都应该持续读。不过，要明确的是，持续读是手段而已，核心还是要持续更新自己的知识，与时俱进吧，别弄得跟每天打卡完成任务一样，意义不大，但凡做技术，只有持续学习和精进，自己才能保持自己的竞争力，这个应该是大家的共识吧。")])]),t._v(" "),r("p",[r("strong",[t._v("首先，论文代表的是最前沿，且经过一定实验和同行评审的文章（说实话，论文已经算很靠谱很严谨的东西了），退一步在落地中用处不大，也在特定情况下能生效，只有学习前沿才能保证自己的技术前沿。")])]),t._v(" "),r("p",[r("strong",[t._v("其次，除了要关注论文做了什么，还需要关心论文的动机和思维过程。可能模型本身我们不一定能吸纳，但是动机和思维过程基本上可以参考的，他发现了当前方案的什么问题，考虑怎么解决的，我们可能用不上最终的方法，但是这些思考角度和解决思路是可以学习借鉴的，说不定我们的方案就有这些问题。")])]),t._v(" "),r("p",[r("strong",[t._v("再者，论文中的实验思路，甚至是超参之类的其实都是可以参考的。例如很多新手不知道怎么调bert（狗头），参数其实都在论文里有（没错，说的就是我），一些模型基本的打开方式都在论文里了。另外，一些效果的论证实验，自己平时也可以尝试，某个方法有没有用，可以用相似的方式探索。")])]),t._v(" "),r("p",[r("strong",[t._v("当然，论文的文献综述中会讨论大量相似相关的工作，我自己是挺喜欢读这块的，因为读这里能一次看好多论文，这里的总结前人的工作也是对前人工作的评论，有没有用，有没有被关注，有没有什么缺点等。但注意的是，这里有一些可能不太客观，是比较主观的评价，因为带有视角和立场，综合的其实可以通过原论文的总结和结论综合形成自己的判断。")])]),t._v(" "),r("p",[r("strong",[t._v("说白了，某种程度上，算法工程师在技术上的成长，就是丰富自己武器库的过程，多几个招数，用的时候能多尝试几个，也能多一些思考和探索问题的方式，更容易出效果，更稳妥。")])]),t._v(" "),r("p",[r("strong",[t._v("又一篇地铁文哈哈哈哈，快到站了，写到这。")])])])}),[],!1,null,null,null);n.default=o.exports}}]);